# cal the average score and std with 4 decimal places
import numpy as np

def cal(ori, ela):
    ori_avg = np.mean(ori)
    ela_avg = np.mean(ela)
    ori_std = np.std(ori)
    ela_std = np.std(ela)
    print(f"Original: avg={ori_avg:.3f}, std={ori_std:.3f}")
    print(f"Enhanced: avg={ela_avg:.3f}, std={ela_std:.3f}")

cal(ori, ela)


